data.command:
/*
	You have learned to regain some of your magical energy by studying your spellbook.
	Once per day when you finish a short rest, you can choose expended spell slots to recover.
	The spell slots can have a combined level that is equal to or less than half your wizard level (rounded up),
	and none of the slots can be 6th level or higher.
*/

const spells = duplicate(actor.getRollData().spells);
const wizLevel = actor.getRollData().classes.wizard.levels;

const lvl = [1, 2, 3, 4, 5, 6].filter(i => spells[`spell${i}`].max > 0);
const val = lvl.map(i => spells[`spell${i}`].value);
const max = lvl.map(i => spells[`spell${i}`].max);

if(lvl.filter(i => spells[`spell${i}`].value !== spells[`spell${i}`].max).length < 1) return ui.notifications.error("You are not missing any valid spell slots.");
const roll = await item.roll();
if(!roll) return;

const maxVal = Math.ceil(wizLevel/2);
let spent = 0;

function nth(n){return n + (["st","nd","rd"][((n+90)%100-10)%10-1]||"th")}

let content = `<div name="header"><p>Recovering spell slots: <strong>${spent}</strong> / ${maxVal}.</p></div><table>`;
for(let i = 0; i < lvl.length; i++){
	content += `<tr><td style="text-align: center"><strong>${nth(i+1)}-level:</strong></td><td>`;
	for(let j = 0; j < max[i]; j++) content += `<input type="checkbox" value="${i+1}" name="level${lvl[i]}" ${j < val[i] ? 'checked disabled' : ''}/>`;
	content += `</td></tr>`;
}
content += `</table>`;

const dialog = new Dialog({
	title: "Arcane Recovery",
	content,
	buttons: {
		ok: {
			icon: `<i class="fas fa-check"></i>`,
			label: "Recover",
			callback: async (html) => {
				if(spent > maxVal || spent === 0){
					ui.notifications.error("Invalid number of slots to recover.");
					dialog.render(true);
				} else {
					for(let i = 1; i <= 9; i++) spells[`spell${i}`].value = html[0].querySelectorAll(`input[name=level${i}]:checked`).length;
					await actor.update({"data.spells": spells});
					return ui.notifications.info("Spell slots recovered!");
				}
			}
		}
	},
	render: (html) => {
		html[0].querySelectorAll("input[type=checkbox]").forEach(input => {
			input.addEventListener('change', function(){
				spent = lvl.reduce((acc, e) => acc += Number(html[0].querySelectorAll(`input[name=level${e}]:checked:not(:disabled)`).length * e), 0);
				console.log(spent);
				html[0].querySelector("div[name=header]").innerHTML = `<p>Recovering spell slots: <strong>${spent}</strong> / ${maxVal}.</p>`;
			})
		})
	}
});

dialog.render(true);
