macro.command:
// Roll current item
const roll = await item.roll();
if(!roll) return;

// Increase exhaustion level
let exhaustion = actor.system.attributes.exhaustion;
exhaustion = Math.min(exhaustion + 1, 6);
await actor.update({"system.attributes.exhaustion": exhaustion});
await ChatMessage.create({
    speaker: ChatMessage.getSpeaker(),
    content: `${actor.name} now has an exhaustion level of ${exhaustion}`
});
