macro.command:
const attacker = token; //selected token
const target = game.user.targets.first(); //targeted creature

const skilltoberolled = target.actor.system.skills.ath.total < target.actor.system.skills.acr.total ? "acr" : "ath";

await game.MonksTokenBar.requestContestedRoll({
    token:attacker,
    request: 'skill:ath'
},{
    token: target,
    request:`skill:${skilltoberolled}`
},{
    silent:true, 
    fastForward:false,
    flavor: `${attacker.name} tries to shove ${target.name}.`, 
});
