command:
/*
 * This macro helps you choose your healing potions
 * To use, select your token on the map and then run this macro
 * If you have only one type of healing potion on your character, it will be used
 * If you have several types of healing potions on your character, you will be asked which one to use
*/

// Only one token can be selected
if(canvas.tokens.controlled.length != 1){
    ui.notifications.error("Please select a single token.");
    return;
}

// Definitions
let s_actor = canvas.tokens.controlled[0].actor
const potions = s_actor.itemTypes.consumable.filter(i => i.data.data.consumableType === "potion" && i.data.data.actionType === "heal");

if(potions.length === 0){
    ui.notifications.error("You don't have any healing potions.");
    return;
}

if(potions.length === 1){
    s_actor.items.get(potions[0].id).roll();
    return;
}

// Create the options available for the prompt
let confirmed = false;
const optionsText = potions.reduce((acc, e) => acc += `<option value="${e.id}">${e.data.name}</option>`, ``);

// Create a dialogue box to select from available potions
new Dialog({
    title: "Macro: Healing Potion",
    content: `
    <form id="use-potion-form">
        <p>` + game.i18n.format("Select which potion you'd like to use.") + `</p>
        <div class="form-group">
            <label>Potion Strength</label>
            <div class="form-fields">
                <select name="potion-strength">` + optionsText + `</select>
            </div>
        </div>
        <div style="height:12px;"></div>
    </form>
    `,
    buttons: {
        one: {
            icon: '<i class="fas fa-check"></i>',
            label: "Heal",
            callback: () => confirmed = true
        },
        two: {
            icon: '<i class="fas fa-times"></i>',
            label: "Cancel",
            callback: () => confirmed = false
        }
    },
    default: "Cancel",
    close: html => {
        if (confirmed) {
            const id = html[0].querySelector(`[name=potion-strength]`).value;
            s_actor.items.get(id).roll();
        }
    }
}).render(true);
